import { test, expect } from '@playwright/test';

test.describe('Epic 1: User Account Creation', () => {
  test('1.1 Successful Account Creation (Happy Path)', async ({ page }) => {
    await page.goto('http://127.0.0.1:7860/');

    // Fill out the account creation form
    await page.getByLabel('Username').fill('traderQA');
    await page.getByLabel('Password').fill('password123');
    await page.getByLabel('Initial Deposit Amount ($)').fill('10000');
    await page.getByRole('button', { name: 'Create Account' }).click();

    // Check for success message on the creation screen
    await expect(page.getByLabel('Status')).toHaveValue("Success: Account 'traderQA' created with an initial deposit of $10,000.00.", { timeout: 10000 });

    // Verify UI transitions to the main interface and Portfolio tab is active
    await expect(page.getByRole('tab', { name: 'Portfolio', selected: true })).toBeVisible();

    // Verify values on the Portfolio tab
    await expect(page.getByLabel('Cash Balance ($)')).toHaveValue('$10,000.00');
    await expect(page.getByLabel('Total Portfolio Value ($)')).toHaveValue('$10,000.00');
    await expect(page.getByLabel('Total Profit / Loss ($)')).toHaveValue('$0.00');
  });
});

import { test, expect } from '@playwright/test';

test.describe('Epic 1: User Account Creation', () => {
  test('1.2 Attempt to Create Account with a Duplicate Username', async ({ page }) => {
    // Seeding Step: Create the initial user.
    await page.goto('http://127.0.0.1:7860/');
    await page.getByLabel('Username').fill('existingUser');
    await page.getByLabel('Password').fill('password123');
    await page.getByLabel('Initial Deposit Amount ($)').fill('5000');
    await page.getByRole('button', { name: 'Create Account' }).click();
    await expect(page.getByRole('tab', { name: 'Portfolio' })).toBeVisible();

    // Navigate back to the creation page to attempt creating a duplicate
    await page.goto('http://127.0.0.1:7860/');

    // Attempt to create the same user again
    await page.getByLabel('Username').fill('existingUser');
    await page.getByLabel('Password').fill('any-password');
    await page.getByLabel('Initial Deposit Amount ($)').fill('1000');
    await page.getByRole('button', { name: 'Create Account' }).click();

    // Check for the error message
    await expect(page.getByLabel('Status')).toHaveValue("Error: Username 'existingUser' is already taken. Please choose a different username.");

    // Verify the user remains on the account creation screen
    await expect(page.getByRole('button', { name: 'Create Account' })).toBeVisible();
    await expect(page.getByRole('tab', { name: 'Portfolio' })).not.toBeVisible();
  });
});

import { test, expect } from '@playwright/test';

test.describe('Epic 1: User Account Creation', () => {
  const username = 'newUser123';
  const password = 'password123';

  async function attemptCreation(page, depositAmount) {
    await page.goto('http://127.0.0.1:7860/');
    await page.getByLabel('Username').fill(username);
    await page.getByLabel('Password').fill(password);
    await page.getByLabel('Initial Deposit Amount ($)').fill(depositAmount);
    await page.getByRole('button', { name: 'Create Account' }).click();
  }

  test('1.3 Test Case A: Attempt to create account with zero deposit', async ({ page }) => {
    await attemptCreation(page, '0');
    await expect(page.getByLabel('Status')).toHaveValue('Error: Initial deposit must be a positive number.');
    await expect(page.getByRole('button', { name: 'Create Account' })).toBeVisible();
  });

  test('1.3 Test Case B: Attempt to create account with negative deposit', async ({ page }) => {
    await attemptCreation(page, '-100');
    await expect(page.getByLabel('Status')).toHaveValue('Error: Initial deposit must be a positive number.');
    await expect(page.getByRole('button', { name: 'Create Account' })).toBeVisible();
  });

  test('1.3 Test Case C: Attempt to create account with non-numeric deposit', async ({ page }) => {
    await attemptCreation(page, 'abc');
    // Depending on implementation, this might be prevented by the input type or result in a different error.
    // We test for the specified error message.
    await expect(page.getByLabel('Status')).toHaveValue('Error: Initial deposit must be a positive number.');
    await expect(page.getByRole('button', { name: 'Create Account' })).toBeVisible();
  });
});
